mapper.select:
SELECT DISTINCT smsss.device_type AS deviceType, dt.name AS deviceTypeName, SUM(smsss.total) AS tradeTotal, smsss.un_active_days as unActiveDays, smsss.active_or_not as activeOrNot, smsss.created_date as statTime FROM stk_merchant_stock_sn_stat AS smsss LEFT JOIN device_type AS dt ON(smsss.device_type = dt.ID)
and smsss.un_active_days = #{unActiveDays,jdbcType=INTEGER}
and smsss.active_or_not = #{activeOrNot,jdbcType=VARCHAR}
group by smsss.device_type order by smsss.id desc

select smsss.device_type as deviceType, dt.name AS deviceTypeName, smsss.to_merchant_code as toMerchantCode, smsss.to_merchant_name as toMerchantName, smsss.total as tradeTotal, smsss.un_active_days as unActiveDays, smsss.active_or_not as activeOrNot, smsss.created_date as statTime FROM stk_merchant_stock_sn_stat AS smsss LEFT JOIN device_type AS dt ON(smsss.device_type = dt.ID)
and smsss.un_active_days = #{unActiveDays,jdbcType=INTEGER}
and smsss.active_or_not = #{activeOrNot,jdbcType=VARCHAR}
order by smsss.id desc

select smsss.device_type as deviceType, dt.name AS deviceTypeName, smsss.to_merchant_code as toMerchantCode, smsss.to_merchant_name as toMerchantName, smsss.total as tradeTotal, smsss.un_active_days as unActiveDays, smsss.active_or_not as activeOrNot, smsss.created_date as statTime FROM stk_merchant_stock_sn_stat AS smsss LEFT JOIN device_type AS dt ON(smsss.device_type = dt.ID)
and smsss.un_active_days = #{unActiveDays,jdbcType=INTEGER}
and smsss.active_or_not = #{activeOrNot,jdbcType=VARCHAR}
order by smsss.id desc
