mapper.select:
select smww.merchant_code as merchantCode, smww.merchant_name as merchantName, smww.warning_month as warningMonth, smww.device_type as deviceType, smww.waresale_rate as stockSaleRatio, bdui.channel as channelId, dt.name as deviceTypeName from stk_merchant_warning_waresale as smww left join bs_dealer_user_info as bdui on(smww.merchant_code=bdui.merchant_code) left join device_type as dt on(dt.id=smww.device_type)
and bdui.channel = #{merchantChannelId,jdbcType=TINYINT}
and (smww.merchant_code like concat('%',#{merchantCode,jdbcType=VARCHAR},'%')
or smww.merchant_name like concat('%',#{merchantName,jdbcType=VARCHAR},'%'))
and smww.device_type = #{deviceType,jdbcType=INTEGER}
and smww.warning_month = #{warningMonth,jdbcType=VARCHAR}
order by smww.id desc

select smww.merchant_code as merchantCode, smww.merchant_name as merchantName, smww.warning_month as warningMonth, smww.device_type as deviceType, smww.waresale_rate as stockSaleRatio, bdui.channel as channelId, dt.name as deviceTypeName from stk_merchant_warning_waresale as smww left join bs_dealer_user_info as bdui on(smww.merchant_code=bdui.merchant_code) left join device_type as dt on(dt.id=smww.device_type)
and bdui.channel = #{merchantChannelId,jdbcType=TINYINT}
and (smww.merchant_code like concat('%',#{merchantCode,jdbcType=VARCHAR},'%')
or smww.merchant_name like concat('%',#{merchantName,jdbcType=VARCHAR},'%'))
and smww.device_type = #{deviceType,jdbcType=INTEGER}
and smww.warning_month = #{warningMonth,jdbcType=VARCHAR}
order by smww.id desc
