mapper.select:
select
from ec_merchant_order as e left join bs_dealer_user_info as u on(e.MERCHANT_CODE=u.MERCHANT_CODE) where e.DELETED_FLAG="N"
AND e.ORDER_NUMBER like "%"#{orderNumber,jdbcType=VARCHAR}"%"
AND e.material_code like "%"#{materialCode,jdbcType=VARCHAR}"%"
AND e.material_name like "%"#{materialName,jdbcType=VARCHAR}"%"
AND e.PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}
AND e.DEVICE_TYPE =#{deviceType,jdbcType=VARCHAR}
AND e.CHANNEL =#{channel,jdbcType=VARCHAR}
AND e.MERCHANT_CODE =#{merchantCode,jdbcType=VARCHAR}
AND e.MERCHANT_NAME like "%"#{merchantName,jdbcType=VARCHAR}"%"
AND e.STATUS = #{status,jdbcType=VARCHAR}
AND CONVERT(DATE_FORMAT(e.ORDER_TIME,'%Y%m%d%H%i%s'),SIGNED) >= CONVERT(DATE_FORMAT(#{startDate},'%Y%m%d%H%i%s'),SIGNED)
AND CONVERT(DATE_FORMAT(e.ORDER_TIME,'%Y%m%d%H%i%s'),SIGNED) <= CONVERT(DATE_FORMAT(#{endDate},'%Y%m%d%H%i%s'),SIGNED)
AND CONVERT(DATE_FORMAT(e.CHECK_TIME,'%Y%m%d%H%i%s'),SIGNED) >= CONVERT(DATE_FORMAT(#{checkStartDate},'%Y%m%d%H%i%s'),SIGNED)
AND CONVERT(DATE_FORMAT(e.CHECK_TIME,'%Y%m%d%H%i%s'),SIGNED) <= CONVERT(DATE_FORMAT(#{checkEndDate},'%Y%m%d%H%i%s'),SIGNED)
GROUP BY e.ORDER_NUMBER ORDER BY e.CREATED_DATE desc
