mapper.select:
select

from bs_merchant_stock


		AND MERCHANT_NAME LIKE CONCAT('%',#{merchantName},'%')	


		AND MERCHANT_CODE = #{merchantCode,jdbcType=VARCHAR}


		AND MATERIAL_CODE LIKE CONCAT('%',#{materialCode},'%')	 


		AND MATERIAL_NAME LIKE CONCAT('%',#{materialName},'%')	 


		AND MATERIAL_DEVICE_TYPE_ID = #{materialDeviceTypeId,jdbcType=INTEGER}


		AND MATERIAL_DEVICE_TYPE_NAME = #{materialDeviceTypeName,jdbcType=VARCHAR}


		AND STAT_STCK_NUM >=  #{minStockNum,jdbcType=INTEGER}


		AND STAT_STCK_NUM <=  #{maxStockNum,jdbcType=INTEGER}


		AND DELETED_FLAG = #{deletedFlag,jdbcType=VARCHAR}


order by ID desc

select 

from bs_merchant_stock 


		AND MERCHANT_NAME LIKE CONCAT('%',#{merchantName},'%')	


		AND MERCHANT_CODE = #{merchantCode,jdbcType=VARCHAR}


		AND MATERIAL_CODE LIKE CONCAT('%',#{materialCode},'%')	 


		AND MATERIAL_NAME LIKE CONCAT('%',#{materialName},'%')	 


		AND MATERIAL_DEVICE_TYPE_ID = #{materialDeviceTypeId,jdbcType=INTEGER}


		AND MATERIAL_DEVICE_TYPE_NAME = #{materialDeviceTypeName,jdbcType=VARCHAR}


		AND STAT_STCK_NUM >=  #{minStockNum,jdbcType=INTEGER}


		AND STAT_STCK_NUM <=  #{maxStockNum,jdbcType=INTEGER}


		AND DELETED_FLAG = #{deletedFlag,jdbcType=VARCHAR}


order by ID desc;

select sum(STAT_SELL_NUM) as statSellNum,sum(STAT_RETN_NUM) as statRetnNum, sum(STAT_SETT_NUM) as statSettNum, sum(STAT_CAIN_NUM) as statCainNum,
sum(STAT_CAOU_NUM) as  statCaouNum, sum(STAT_STCK_NUM) as statStckNum from bs_merchant_stock


		AND MERCHANT_CODE = #{merchantCode,jdbcType=VARCHAR}


		AND MATERIAL_CODE = #{materialCode,jdbcType=VARCHAR}


		AND DELETED_FLAG = #{deletedFlag,jdbcType=VARCHAR}
