mapper.select:
select jsar.ID, jsar.STATUS, jsar.CHECK_FAIL_RESULT,
from jst_shop js left join jst_shop_agent_relation jsar on js.SHOP_CODE = jsar.SHOP_CODE where js.DELETED_FLAG = 'N' and jsar.DELETED_FLAG = 'N'
and jsar.AGENT_MERCHANT_CODE = #{agentMerchantCode,jdbcType=VARCHAR}
and js.SHOP_NAME LIKE "%"#{shopName,jdbcType=VARCHAR}"%"
and js.STATUS = #{status,jdbcType=VARCHAR}

select jsar.ID, jsar.STATUS, jsar.CHECK_FAIL_RESULT, jsar.AGENT_MERCHANT_CODE, jsar.AGENT_MERCHANT_NAME,
from jst_shop js left join jst_shop_agent_relation jsar on js.SHOP_CODE = jsar.SHOP_CODE where js.DELETED_FLAG = 'N' and jsar.DELETED_FLAG = 'N'
and jsar.AGENT_MERCHANT_CODE = #{agentMerchantCode,jdbcType=VARCHAR}
and jsar.AGENT_MERCHANT_NAME LIKE "%"#{agentMerchantName,jdbcType=VARCHAR}"%"
and js.SHOP_NAME LIKE "%"#{shopName,jdbcType=VARCHAR}"%"
and js.STATUS = #{status,jdbcType=VARCHAR}
